import React, { bind, } from 'react';
import Axios from 'axios';
import {motion} from 'framer-motion';

import {FaPhoneAlt, FaInstagram, FaLinkedin, FaGithub} from 'react-icons/fa';
import './Contact.css';
import '../components/Footer/Footer.css';

import letter from '../Images/letter.png';


class Contact extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            message: '',
            disabled: false,
            emailSent: null,
        };

        this.handleChange = this.handleChange.bind(this);

    }

    handleChange(event,field) {
        this.setState({ [field]: event.target.value});
      }

      handleSubmit = (event) => {
          event.preventDefault();

          this.setState({
              disabled: true,       
          });

              Axios.post('http://localhost:3030/api/daniel', this.state)
              .then(res => {
                  if(res.data.success) {
                this.setState({
                    disabled: false,
                    emailSent:true
                });
            } else {
                    this.setState({
                        disabled: false,
                        emailSent:false
                    });
                }           
              })
              .catch(err => {
                  this.setState({
                      disabled:false,
                      emailSent: false
                  });
              })

      }

   render() {
    const transition = { duration: 1.4, ease: [0.6, 0.01, -0.05, 0.9 ]};
    const exit = {
        exit: {
        opacity: 0,
        }
    };
    return(

          <div className="name-box">
          <form name="simpleContactForm" method="POST" id="simple-contact-form" class="contact-form">
    <p class="form-row">
        <label id="contact-form-name-label" for="contact-form-name" class="form-label">Name</label>
        <input type="text" name="name" id="contact-form-name" aria-labelledby="contact-form-name-label" class="form-input" />
    </p>
    <p class="form-row">
        <label id="contact-form-email-label" for="contact-form-email" class="form-label">Email address</label>
        <input type="email" name="email" id="contact-form-email" aria-labelledby="contact-form-email-label" class="form-input" />
    </p>
    <p class="form-row">
        <label id="contact-form-message-label" for="contact-form-message" class="form-label">Message</label>
        <textarea
            name="message"
            id="contact-form-message"
            aria-labelledby="contact-form-message-label"
            class="form-textarea"
            rows="7"
        ></textarea>
    </p>
    <p class="form-row form-submit">
        <button type="submit" class="button">Send Message</button>
    </p>
</form>

       </div>
    );
    }
}

export default Contact;
