import React from 'react';
import {Link} from 'react-router-dom';

import './SideDrawer.css'

const SideDrawer = props => {
  let drawerClasses = 'side-drawer';
  if (props.show) {
    drawerClasses = 'side-drawer open';
  }
  state = {
    sideDrawerOpen: false
  }  

  // Hamburger menu button handler
 drawerToggleClickHandler = () => {
   this.setState((prevState) => {
     return {sideDrawerOpen: !prevState.sideDrawerOpen};
   });
 };

 //closes hamburger menu
 backdropClickHandler = () => {
   this.setState({sideDrawerOpen: false})
 };

 if (this.state.sideDrawerOpen) {
  linkclose =    <SideDrawer click={this.backdropClickHandler} />
}

  return (
  <nav className={drawerClasses}>

      <ul>
         <li> <Link className="linkclose" onClick={props.click} to="/home"> Home </Link> </li >
         <li> <Link className="linkclose" onClick={props.click} to="/about"> About </Link> </li >
         <li> <Link className="linkclose" onClick={props.click} to="/contact"> Contact </Link> </li >
      </ul>
  </nav>
  );
};

export default SideDrawer;
